import React from "react";
import SelectRate from './SelectRate.js';
import SelectCurrency from './SelectCurrency.js';
import './CurrentRate.css';



let currencyList = [];

class CurrentRate extends React.Component {
    constructor() {
        super()
        this.state = {
            loading: true,
           currencies: null,
           inputInitial: 1,
           inputResult: 1,
           selectedFirstOption: null,
           selectedSecondOption: null
        };
        this.handleChange = this.handleChange.bind(this);
    }


    async componentDidMount() {
        const url = "https://bank.gov.ua/NBUStatService/v1/statdirectory/exchange?json";
        const response = await fetch(url);
        const data = await response.json();
        this.setState({ currencies: data, loading: false });
    }

    handleChange = selectedOption => {
        this.setState({ selectedOption });
        console.log(`Option selected:`, selectedOption);
    };


    render() {
        currencyList = this.state.currencies;
        currencyList.forEach(elem => {
            elem.value
        });
        if (this.state.loading) {
            return <div>loading...</div>;
          }

          if (!this.state.currencies) {
            return <div>didn't get a currency</div>;
          }

        return (
            <div>
                <h2>Exchange</h2>
                <div className="currency_exchange">
                    <div>
                        <span>Sell</span>
                        <div>
                        <SelectCurrency currencies={this.state.currencies}/>
                        </div>
                        <input 
                            type="text" 
                            value={this.state.inputInitial} 
                            className="soldCurrency"
                            />
                    </div>
                    <div>

                    </div>
                    <button>change</button>
                    <div>
                        <span>Buy</span>
                        <div>
                        <SelectCurrency currencies={this.state.currencies}/>
                        </div>
                        <input 
                            type="text"
                            value={this.state.inputResult}
                        />
                    </div>
                </div>

                <h2>Current exchange rate</h2>
                <SelectRate currencies={this.state.currencies} />
                <div>
            </div>
        </div>

        )
    }

}



export default CurrentRate
