import React from "react";

class  SelectCurrency extends React.Component {
    constructor(props) {
      super(props)
      this.state = {
        currencies: this.props.value,
        inputInitial: "1",
        inputResult: null,
        value: 'UAH',
        value2: 'USD',
        initialRate: 0

      }
      this.handleChange = this.handleChange.bind(this);
      this.handleChange2 = this.handleChange2.bind(this);
      this.inputChange = this.inputChange.bind(this);

    }



    handleChange(event) {
      this.setState({value: event.target.value});
    }

    handleChange2(event) {
      let rate;
      this.state.currencies.forEach(item => {
          if(item.cc === event.target.value) {
              rate = item.rate;
          }
      });
    this.setState(
        {value2: event.target.value,
          initialRate: rate,
          inputResult: rate * +this.state.inputInitial
      }); 

  }

  inputChange(event) {
      this.setState({
          inputInitial: event.target.value,
          inputResult: this.state.initialRate * +event.target.value
      });
  }

    render() {
        let currencyList = this.props.currencies;
        const uah = "UAH";
        let rateUSD;
      this.state.currencies.forEach(item => {
          if(item.cc === "USD") {
              rateUSD = item.rate;
          }
      });
      this.setState({ inputResult: rateUSD, initialRate: rateUSD });
        return (
          <div className="currency_exchange">
          <div>
              <span>Sell</span>
              <div>
              <select value={this.state.value} onChange={this.handleChange}>
                  {
                      currencyList.map((elem, index)=>{
                      return (<option key={index} value={elem.cc} rate={elem.rate}>
                          {elem.cc}
                      </option>)
                      })
                  }
                  <option key={currencyList.length} value={uah}>
                      {uah}
                  </option>
              </select>
              </div>
              <input 
                  type="text" 
                  value={this.state.inputInitial} 
                  className="soldCurrency"
                  onChange={this.inputChange}
                  />
          </div>
          <div>

          </div>
          <button>change</button>
          <div>
              <span>Buy</span>
              <div>
              <select value={this.state.value2} onChange={this.handleChange2} rate={this.state.inputResult}>
                  {
                      currencyList.map((elem, index)=>{
                      return (<option key={index} value={elem.cc} rate={elem.rate}>
                          {elem.cc}
                      </option>)
                      })
                  }
                  <option key={currencyList.length} value={uah}>
                      {uah}
                  </option>
              </select>
              </div>
              <input 
                  type="text"
                  value={this.state.inputResult}
              />
          </div>
      </div>
        );
    }
}

export default SelectCurrency;
