import React from "react";

class  SelectCurrency extends React.Component {
    constructor(props) {
      super(props)
      this.state = {
        sellInputValue: 1,
        buy: null,
        value: 'UAH',
        value2: 'USD',
        initialRate: 0,
        firstInputRate: 1     
      }
      this.handleChange = this.handleChange.bind(this);
      this.handleChange2 = this.handleChange2.bind(this);
      this.inputChange = this.inputChange.bind(this);

    }

    async componentDidMount() {
      this.props.currencies.push({"rate": 1, "cc": "UAH"});
      let rateUSD;
      this.props.currencies.forEach(item => {
          if(item.cc === "USD") {
              rateUSD = item.rate;
          }
      });
      this.setState({ inputResult: 1 / rateUSD, initialRate: rateUSD });
    }

    handleChange(event) {
        let firstRate;
        this.props.currencies.forEach(item => {
            if(item.cc === event.target.value) {
                firstRate = item.rate;
            }
        });
      this.setState({
          value: event.target.value, 
          firstInputRate: firstRate, 
          inputResult: firstRate * this.state.sellInputValue / this.state.initialRate});
    }

    handleChange2(event) {
      let rate;
      this.props.currencies.forEach(item => {
          if(item.cc === event.target.value) {
              rate = item.rate;
          }
      });
    this.setState(
        {value2: event.target.value,
          initialRate: rate,
          inputResult: this.state.firstInputRate * this.state.sellInputValue / rate
      });   
  }

  inputChange(event) {
      this.setState({
          inputInitial: event.target.value,
          inputResult: this.state.firstInputRate * event.target.value / this.state.initialRate
      });
  }

    render() {
        let currencyList = this.props.currencies;

        return (
          <div className="currency_exchange">
          <div>
              <span>Sell</span>
              <div>
              <select value={this.state.value} onChange={this.handleChange}>
                  {
                      currencyList.map((elem, index)=>{
                      return (<option key={index} value={elem.cc} rate={elem.rate}>
                          {elem.cc}
                      </option>)
                      })
                  }
              </select>
              </div>
              <input 
                  type="number" 
                  value={this.state.sellInputValue} 
                  className="soldCurrency"
                  onChange={this.inputChange}
                  />
          </div>
          <div>

          </div>
          <button>change</button>
          <div>
              <span>Buy</span>
              <div>
              <select value={this.state.value2} onChange={this.handleChange2} rate={this.state.inputResult}>
                  {
                      currencyList.map((elem, index)=>{
                      return (<option key={index} value={elem.cc} rate={elem.rate}>
                          {elem.cc}
                      </option>)
                      })
                  }
              </select>
              </div>
              <input 
                  type="number"
                  value={this.state.inputResult}
              />
          </div>
      </div>
        );
    }
}

export default SelectCurrency;
