import React from "react";
import SelectRate from './SelectRate.js';
import SelectCurrency from './SelectCurrency.js';
import './CurrentRate.css';



let currencyList = [];

class CurrentRate extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            loading: true,
           currencies: null,
           inputInitial: "1",
           inputResult: null,
           value: 'UAH',
           value2: 'USD',
           initialRate: 0
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleChange2 = this.handleChange2.bind(this);
        this.inputChange = this.inputChange.bind(this);
    }


    async componentDidMount() {
        const url = "https://bank.gov.ua/NBUStatService/v1/statdirectory/exchange?json";
        const response = await fetch(url);
        const data = await response.json();
        let rateUSD;
        data.forEach(item => {
            if(item.cc === "USD") {
                rateUSD = item.rate;
            }
        });
        this.setState({ currencies: data, loading: false, inputResult: rateUSD });
    }

    handleChange(event) {
        this.setState({value: event.target.value});
    }

    handleChange2(event) {
        this.state.currencies.forEach(item => {
            if(item.cc === event.target.value) {
                rate = item.rate;
            }
        });
      this.setState(
          {value2: event.target.value,
            initialRate: rate,
            inputResult: rate * +this.state.inputInitial
        }); 

    }

    inputChange(event) {
        this.setState({
            inputInitial: event.target.value,
            inputResult: rate * +this.state.inputInitial
        });
    }


    render() {
        currencyList = this.state.currencies;
        const uah = "UAH";
        if (this.state.loading) {
            return <div>loading...</div>;
          }

          if (!this.state.currencies) {
            return <div>didn't get a currency</div>;
          }

        return (
            <div>
                <h2>Exchange</h2>
                <div className="currency_exchange">
                    <div>
                        <span>Sell</span>
                        <div>
                        <select value={this.state.value} onChange={this.handleChange}>
                            {
                                currencyList.map((elem, index)=>{
                                return (<option key={index} value={elem.cc} rate={elem.rate}>
                                    {elem.cc}
                                </option>)
                                })
                            }
                            <option key={currencyList.length} value={uah}>
                                {uah}
                            </option>
                        </select>
                        </div>
                        <input 
                            type="text" 
                            value={this.state.inputInitial} 
                            className="soldCurrency"
                            onChange={this.inputChange}
                            />
                    </div>
                    <div>

                    </div>
                    <button>change</button>
                    <div>
                        <span>Buy</span>
                        <div>
                        <select value={this.state.value2} onChange={this.handleChange2} rate={this.state.inputResult}>
                            {
                                currencyList.map((elem, index)=>{
                                return (<option key={index} value={elem.cc} rate={elem.rate}>
                                    {elem.cc}
                                </option>)
                                })
                            }
                            <option key={currencyList.length} value={uah}>
                                {uah}
                            </option>
                        </select>
                        </div>
                        <input 
                            type="text"
                            value={this.state.inputResult}
                        />
                    </div>
                </div>

                <h2>Current exchange rate</h2>
                <SelectRate currencies={this.state.currencies} />
                <div>
            </div>
        </div>

        )
    }

}



export default CurrentRate
